import { createSlice} from "@reduxjs/toolkit";

const intialDataCartState = {items: [], totalAmount:0};

const dataCartSlice = createSlice({
    name:'dataCart',
    initialState: intialDataCartState,
    reducers: {
        addItemToCart(state,action){
        const newItem = action.payload;
        const existingItem = state.items.find(item => item.id === newItem.id)
        if(!existingItem){
            state.items.push({...newItem, quantity:0, totalPrice:newItem.price})
        }
        else {
           existingItem.quantity++
           existingItem.totalPrice = existingItem.totalPrice + newItem.price
        }
    },
    removeItemFromCart(state,action){
        console.log('test')
    }
}
});

export default dataCartSlice
export const dataCartActions = dataCartSlice.actions;
