import { createSlice} from "@reduxjs/toolkit";

const intialDataCartState = {items: [], totalAmount:0};

const dataCartSlice = createSlice({
    name:'dataCart',
    initialState: intialDataCartState,
    reducers: {
        addItemToCart(state,action){
        console.log('test')
    },
    removeItemFromCart(state,action){
        console.log('test')
    }
}
});

export default dataCartSlice.reducer
export const showCartActions = dataCartSlice.actions;
