import ProductItem from './ProductItem';
import classes from './Products.module.css';
import { useEffect } from 'react';
import { useDispatch } from 'react-redux/es/exports';
import {dataCartActions} from './../store/cartData-Slice'


const DUMMY_PRODUCTS = [{
  id:'p1',
  price: 6,
  title: 'my first book',
  description: 'the first book i ever wrote'
},
{
  id:'p2',
  price: 5,
  title: 'my second book',
  description: 'the second book i ever wrote'
}]
const Products = (props) => {
  const dispatch = useDispatch();
  useEffect(() => {
    const fetchData= async () => {
      const response = await fetch('https://react-test-aefc2-default-rtdb.firebaseio.com/cart.json')
      if(!response.ok){
        throw new Error('fetching data failed')
      }
      const data = await response.json();
      dispatch(dataCartActions.addItemToCart(data))
      console.log(data)
    }

    fetchData().catch(error => console.log(error.message))
  },[dispatch])
  return (
    <section className={classes.products}>
      <h2>Buy your favorite products</h2>
      <ul>{DUMMY_PRODUCTS.map(item => <ProductItem 
      key={item.id}
      id={item.id}
      price={item.price}
      title = {item.title}
      description= {item.description}
      />)}
      </ul>
    </section>
  );
};

export default Products;
